export async function POST(req: NextRequest) {
    try {
        if (!process.env.OPENAI_API_KEY) {
            return NextResponse.json(
                { error: "OPENAI_API_KEY n├úo configurada no .env.local" },
                { status: 500 }
            );
        }

        const body = await req.json();
        const userMessage: string = body?.message ?? "";
        const history: { role: "user" | "assistant"; content: string }[] =
            body?.history ?? [];

        if (!userMessage || typeof userMessage !== "string") {
            return NextResponse.json(
                { error: "Mensagem inv├ílida" },
                { status: 400 }
            );
        }

        // Detecta modo
        let mode = detectMode(userMessage);
        if (mode === "unknown") {
            // Se estiver amb├¡guo, assumimos que a pessoa ├® cliente do sal├úo.
            mode = "b2c";
        }

        const systemPrompt = getSystemPrompt(mode);

        const messages: OpenAI.Chat.ChatCompletionMessageParam[] = [
            { role: "system", content: systemPrompt },
            ...history,
            { role: "user", content: userMessage },
        ];

        const model = process.env.OPENAI_MODEL || "gpt-4.1-mini";

        const completion = await client.chat.completions.create({
            model,
            messages,
            temperature: 0.7,
        });

        const reply = completion.choices[0]?.message?.content ?? "";

        return NextResponse.json({
            reply,
            mode,
        });
    } catch (err: any) {
        console.error("Erro na /api/chat:", err);
        return NextResponse.json(
            { error: "Erro ao gerar resposta do chat" },
            { status: 500 }
        );
    }
}

